<?phpclass ClaseHTML {    static function submenu($clase, $CRUD, $controles = NULL, $datos = NULL) {        $i = 1;        $id_modulo = 'id_'.$clase;        ?>        <div class="cont_titulo_modulo">            <span class="inset">Administrador de <?php echo ucfirst($clase) ?></span>        </div>        <div id="cont_btn_arriba">            <?php if (in_array('crear', $CRUD)) { ?>                 <span id="btn_anadir_evento"><a href="javascript:nuevo('<?php echo $clase; ?>','0','', '1')" class="inset" onfocus="this.blur();" >Nuevo Elemento</a></span>            <?php } ?>         </div>        <article class="modulo_edicion_txt">            <div class="titulos">                <?php if (in_array('orden', $controles)) { ?>                     <p><b>ORDEN</b></p>                <?php  } ?>                 <p><b>CODIGO</b></p>                <p><b>NOMBRE</b></p>            </div>        </article>        <?php        if ($datos[0] != "") {            foreach ($datos as $dato) {                ?>                  <article class="modulo_edicion_txt">                    <div class="titulos">                        <div class="edit_No_edicion">                            <?php if (in_array('orden', $controles)) { ?>                                 <form>                                    <input class="numero" name="nuevo_orden_<? echo $i; ?>" id="nuevo_orden_<?php echo $i; ?>" type="text" value="<? echo $dato->orden; ?>">                                    <input class="btn" name="<? echo $dato->orden; ?>" type="button" value="Cambiar" onclick="javascript:editarOrden('<?php echo $clase; ?>', '<? echo $dato->$id_modulo; ?>', '<? echo $dato->orden; ?>', '<? echo $i; ?>');" onfocus="this.blur();" >                                </form>                            <?php } ?>                        </div>                        <p><?php echo utf8_encode($dato->$id_modulo); ?></p>                        <p><?php echo utf8_encode($dato->nombre); ?></p>                        <div class="editar">                            <ul>                                <?php if (in_array('estado', $CRUD)) {                                     if ($dato->estado_id == 1) { ?>                                        <li class=" btn"><a href="javascript:publicarItem('<?php echo $clase; ?>','<? echo $dato->$id_modulo; ?>','<? echo $dato->estado_id; ?>')" onfocus="this.blur();">Desactivar<span>Desactivar</span></a></li>                                    <?php } else { ?>                                        <li class="btn"><a href="javascript:publicarItem('<?php echo $clase; ?>','<? echo $dato->$id_modulo; ?>','<? echo $dato->estado_id; ?>')" onfocus="this.blur();" >Activar<span>Activar</span></a></li>                                    <?php }                                 } if (in_array('editar', $CRUD)) { ?>                                    <li class="btn_edit btn"><a href="javascript:javascript:nuevo('<?php echo $clase; ?>','1','<? echo $dato->$id_modulo; ?>', '1');" onfocus="this.blur();">Editar<span>Editar</span></a></li>                                <?php } if (in_array('eliminar', $CRUD)) { ?>                                    <li class="btn_del btn"><a href="javascript:eliminarItem('<?php echo $clase; ?>','<? echo $dato->$id_modulo; ?>');"onfocus="this.blur();" >eliminar<span>Eliminar</span></a></li>                                <?php } ?>                            </ul>                        </div>                    </div>                </article>                <?php $i++;            }            ?>            <div class="clearfix"></div>            <?php        }    }    static function formEdicion($clase, $controles, $customs,$CRUD, $llaves, $listas = NULL, $id = "", $dato = NULL, $edit_footer = '') {        ?>        <div class="cont_titulo_modulo">            <span class="inset">                <?php                $id_modulo = 'id_'.$clase;                if ($id > 0) {                    echo "Edición";                }                else                    echo "Creación";                ?> de <?php echo $clase; ?>            </span>        </div>        <form id="form_item" name="form_item" method="post" enctype="multipart/form-data" action="<?php if ($id == '') echo "index.php?opcion=$clase&a=guardarItem";                else echo "index.php?opcion=$clase&a=editarItem"; ?>">                <div class="cont_campos_variables">                    <?php if (in_array('nombre', $controles)) { ?>                     <div class="edit_column">                        <span class="inset">Nombre</span>                    </div>                    <input type="text" name="nombre" id="nombre" value="<? echo utf8_encode($dato->nombre); ?>">                    <?php } ?>                    <input type="hidden" name="id_item" id="id_item" value="<? echo $id; ?>"/>                </div>                    <?php if (in_array('lista', $controles)) { ?>                     <select>                        <option value ="0" >Seleccionar...</option>                        <?php foreach ($lista as $l) { ?>                        <option value ="<? echo $l->$id_modulo; ?>"><?php echo utf8_encode($l->nombre); ?></option>                        <?php } ?>                    </select>                <?php } ?>                <?php foreach ($listas as $key => $lista) {                    $selected = $llaves[$key];                    $id_lista = strtolower('id_'.$key);                    ?>                    <div class="cont_campos_variables">                        <div class="edit_column">                            <span class="inset"><?php echo $key; ?></span>                        </div>                        <select id="<? echo $llaves[$key]; ?>" name="<? echo $llaves[$key]; ?>" >                            <option value ="0">Seleccionar...</option>                            <?php foreach ($lista as $key => $l) { ?>                                <option value ="<? echo $l->$id_lista; ?>" <?php if($l->$id_lista === $dato->$selected){echo "selected='selected'";}?>><? echo utf8_encode($l->nombre); ?></option>                            <?php }                                                         ?>                        </select>                    </div>                <?php                } ?>                <?php foreach ($customs as $c ) { ?>                    <div class="cont_campos_variables">                        <div class="edit_column">                            <span class="inset"><?php echo $c->nombre; ?></span>                        </div>                        <?php switch ($c->tipo) {                                case 'check':                                   ?>                                   <input type="checkbox" name="<?php echo $c->nombre; ?>" id="<?php echo $c->nombre; ?>"                                         <?php                                         $n = $c->nombre;                                        if($dato->$n === 1) {                                            echo "checked='checked'";                                        }                                    ?> >                                    <?php                                    break;                                default:                                    ?>                                    <input type="text" name="<? echo $c->nombre; ?>" id="<?php echo $c->nombre; ?>" value="<? $n = $c->nombre; echo utf8_encode($dato->$n); ?>">                                    <?php                                break;                            }?>                    </div>                    <?php } ?>            <?php if (in_array('descripcion', $controles)) { ?>                 <div id="cont_editor">                    <div class="titulos"><p>DESCRIPCIÓN</p></div>                    <textarea id="descripcion" name="descripcion" cols="72" rows="17"><? echo $dato->descripcion; ?></textarea>                </div>            <?php } if (in_array('imagen', $controles)) { ?>                 <article class="modulo_edicion">                    <div class="titulos">                        <div class="No_edicion">1</div>                        <p>Imagen (600 de ancho x 200 de alto jpg)</p>                    </div>                    <div class="img_edicion">                        <img src="../media/img/<? echo $clase; ?>/big/<? echo $dato->imagen; ?>" width="100%" height="100%" alt="Inserte una imagen">                    </div>                    <input class="cambiar_imagen" id="imagen" name="imagen" type="file">                    <input type="hidden" name="imagen_ant" id="imagen_ant" value="<? echo $dato->imagen; ?>"/>                </article>            <?php }             if($edit_footer == 'editFooter'){                ClaseHTML::editFooter($clase, $controles, $customs,$CRUD, $llaves, $listas = NULL, $id = "", $dato = NULL);             } ?>            <div id="cont_enviar_editar">                <div id="enviar_editar">                    <div class="cont_checkbox">                        <?php if (in_array('estado', $CRUD)) { ?>                             <span class="inset">Estado</span>                            <input name="estado_id" id="estado_id" type="checkbox" <?                                if ($dato->estado_id == 1) {                                    echo "checked='checked'";                                }                                ?>>                        <?php } ?>                    </div>                    <?php                    if ($id === ""){                        $edicion = '0';                    }else{                        $edicion = '1';                    }                    ?>                    <input id="input_atras" class="inset boton" name="atras" type="button" value="Cancelar" onclick="javascript:window.location = '?opcion=<? echo $clase; ?>';">                    <input id="input_guardar" class="inset boton" name="guardar" type="button" value="Guardar" onclick="javascript:validarBasico('<? echo $edicion ?>')">                </div>            </div>        </form>        <div class="clearfix"></div>        <?php    }    static function editFooter($clase, $controles, $customs,$CRUD, $llaves, $listas = NULL, $id = "", $dato = NULL) {        ?>            <div class="cont_campos_variables">                <div class="edit_column">                    <span class="inset">TOTAL</span>                </div>                <input type="text" id="total" value="<? echo utf8_encode($dato->nombre); ?>" readonly disabled >            </div>            <?php    }}?>